@use '../abstracts/' as *;
@use '../base/' as *;

.btn-wrapper {
  text-align: center;
}

.about-section__content {
  display: flex;
  align-items: center;

  .about-intro {
    width: 45%;
    // margin: auto;
    // text-align: center;

    &__para {
      color: var(--color-text);
      font-size: var(--font-normal);
      width: 100%;
      text-align: justify;
      margin: 2rem 0;
    }
  }

  .about__skills,
  .about__personal {
    width: 45%;
  }

  .about__skills {
    padding-top: 5rem;
    .skill-icon {
      display: inline-block;

      &-svg {
        width: 3.3rem;
      }
    }
    // skill level bars
    .inner-html {
      width: 85%;
    }
    .inner-css {
      width: 85%;
    }
    .inner-js {
      width: 90%;
    }
    .inner-sass {
      width: 80%;
    }
    .inner-ai {
      width: 95%;
    }
    .inner-react {
      width: 30%;
    }

    &--html {
      padding-top: 3rem;
      border-top: 1px solid var(--color-text-offset);
    }

    &--react {
      padding-bottom: 3rem;
      border-bottom: 1px solid var(--color-text-offset);
    }

    &--html,
    &--css,
    &--js,
    &--sass,
    &--ai,
    &--react {
      display: flex;
      justify-content: space-between;
      align-items: center;
      width: 100%;
      margin-bottom: 5rem;
    }

    .skill-bar {
      width: 70%;
      height: 1rem;

      background: var(--color-bg-offset);
      display: flex;
      align-items: center;

      &-inner {
        height: 0.5rem;

        background-image: linear-gradient(
          to right,
          var(--color-primary),
          var(--color-bg)
        );
        background-size: 400%;
      }
    }
  }

  .about__personal {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 50%;
    margin: 3rem auto 5rem;
    border-top: 1px solid var(--color-text-offset);

    &--ul {
      margin-left: 1rem;
      list-style: disc;
      & li {
        // padding-left: 1rem;
        // text-indent: -1rem;

        font-size: var(--font-normal);
        color: var(--color-text-offset);
        margin: 1rem 0;
        text-align: justify;

        & a {
          text-decoration: underline;
          font-style: italic;
        }
      }
    }
  }
}
