@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap');

:root {
  --main-font: 'Poppins', sans-serif;
  --font-small: 1.3rem;
  --font-normal: 1.6rem;
  --font-l: 2rem;
  --font-xl: 2.5rem;
  --font-section-title: 3rem;
  --font-menu: 4.5rem;
  --font-xl: 5rem;
  --font-xxl: 6rem;
}

.section__title {
  text-align: center;
  font-size: var(--font-section-title);
  color: var(--color-text);

  &--icon {
    color: var(--color-primary);
    padding-left: 1.5rem;
  }
}

sources.sass\\abstracts\\_mixins.scss:
@mixin absoluteCenter {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}

// Media Queries Manager
/*
  0 - 600px: Phone
  600px - 900px: Tablet portrait
  901px - 1200px: Tablet landscape
  1201px - 1800px: ~ Lap-Top 15-inches(default styles)
  1801px + : Big desktop
  $breakpoint argument choices: 
  - phone
  - tab-p
  - tab-l
  - big-desk
  1em = 16px
 */
@mixin respond($breakpoint) {
  @if $breakpoint == phone {
    @media (max-width: 37.5em) {
      // 600px
      @content;
    }
  }
  @if $breakpoint == tab-p {
    @media (min-width: 37.51em) and (max-width: 56.25em) {
      // 601px - 900px
      @content;
    }
  }
  @if $breakpoint == tab-l {
    @media (min-width: 56.26em) and (max-width: 75em) {
      // 901px - 1200x
      @content;
    }
  }
  @if $breakpoint == big-desk {
    @media (min-width: 112.5em) {
      // 1800px +
      @content;
    }
  }
}

sources.sass\\base\\_animations.scss:
@keyframes moveInBottom {
  0% {
    opacity: 0;
    transform: translateY(2rem);
  }
  80% {
    transform: translateY(-1rem);
  }

  100% {
    opacity: 1;
    transform: translate(0);
  }
}
@keyframes moveInLeft {
  0% {
    opacity: 0;
    transform: translateX(-10rem);
  }

  80% {
    transform: translateX(1rem);
  }

  100% {
    opacity: 1;
    transform: translate(0);
  }
}

@keyframes moveInRight {
  0% {
    opacity: 0;
    transform: translateX(10rem);
  }

  80% {
    transform: translateX(-1rem);
  }

  100% {
    opacity: 1;
    transform: translate(0);
  }
}

@keyframes down {
  0% {
    transform: translate(0);
  }
  20% {
    transform: translateY(15px);
  }
  40% {
    transform: translate(0);
  }
}

@-webkit-keyframes down {
  0% {
    transform: translate(0);
  }
  20% {
    transform: translateY(15px);
  }
  40% {
    transform: translate(0);
  }
}

sources.sass\\base\\_base.scss:
@use '../abstracts/' as *;
@use './typography' as *;

*,
*::after,
*::before {
  padding: 0;
  margin: 0;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
  scroll-behavior: smooth;
  scroll-padding-top: 70px;
  // media queries
  @include respond(phone) {
    & {
      font-size: 50%;
    }
  }

  @include respond(tab-p) {
    & {
      font-size: 55%;
    }
  }

  @include respond(tab-l) {
    & {
      font-size: 60%;
    }
  }

  @include respond(big-desk) {
    & {
      font-size: 65%;
    }
  }
}

body {
  position: relative;
  background-color: var(--color-bg);
  box-sizing: border-box;
  font-family: var(--main-font);
  //   padding: 2rem;
  // height: 150rem;
  transition: background-color 0.15s;

  overflow-x: hidden;
}

a {
  text-decoration: none;
}

ul {
  list-style: none;
}

.background {
  position: absolute;
  top: 0;
  left: 0;
  @include absoluteCenter;
  width: 100vw;
  height: 100%;
  // background-color: var(--color-bg-offset);
  background-color: var(--color-bg);
  z-index: -1;
  // box-shadow: 0px 2rem 2rem -2rem rgba(13, 43, 90, 0.08);
  // background: url('./../images/pattern.jpg');

  // &:not(:last-child) {
  //   margin-bottom: 5rem;
  // }
}

.divider {
  width: 100%;
  height: 20rem;
  background-color: var(--color-bg-offset);
  // margin-bottom: 2rem;
}
.divider__curved--home {
  clip-path: url(#myCurve);
}
.divider__curved--contact {
  clip-path: url(#myCurveContact);
}
.divider__curved--about {
  clip-path: url(#myCurveAbout);
}
