.thanks {
  &__h1 {
    color: var(--color-text);
    margin: auto;
  }
  &__icon {
    background-color: azure;
    display: flex;
    flex-direction: column;
    align-items: center;
  }
  &__h3 {
    color: var(--color-text);
  }
