@use '../abstracts/' as *;
@use '../base/' as *;
// CV button
.btn-wrapper {
  text-align: center;
}

.about-section {
  // background-color: blanchedalmond;
  // margin-top: -3rem;

  &__content {
    display: flex;
    align-items: center;
    flex-direction: column;
    // background-color: aquamarine;

    &--pair {
      // background-color: #3f7731;
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: space-between;
      margin-bottom: 5rem;
    }
    &--left {
      // background-color: #4c6960;
      width: 50%;

      .about-subtitle {
        font-size: var(--font-l);
        color: var(--color-text);
        font-style: italic;
      }
      .left-intro {
        // width: 50%;

        &__para {
          color: var(--color-text);
          font-size: var(--font-normal);
          width: 100%;
          text-align: justify;
          margin: 2rem 0;
        }
      }

      .left-skills {
        width: 100%;
        display: flex;
        flex-direction: column;
        margin: 3rem 0;

        &__para {
          color: var(--color-text);
          font-size: var(--font-normal);
          width: 100%;
          text-align: justify;
          margin: 2rem 0 4rem;
        }

        .skills {
          display: flex;
          align-items: center;
          justify-content: space-between;
          // background-color: aqua;

          .skill-icon {
            display: block;
            width: 5rem;
            height: 5rem;

            &-svg {
              width: 100%;
              height: 100%;
            }
          }
          // outer - skill bar
          .skill-bar {
            width: 80%;
            height: 1rem;
            background-color: var(--color-bg-offset);
            border-radius: 0.5rem;
            display: flex;
            align-items: center;

            .inner-html {
              width: 80%;
            }

            .inner-css {
              width: 80%;
            }

            .inner-js {
              width: 85%;
            }

            .inner-sass {
              width: 70%;
            }

            .inner-react {
              width: 30%;
            }

            .inner-illustrator {
              width: 85%;
            }
            // inner - skill bar
            &-inner {
              // width: 80%;
              height: 5px;
              background-color: var(--color-primary);
              border-radius: 0.5rem;
              margin-left: 2px;
            }
          }
        }
      }

      .about__personal {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        width: 100%;
        margin: 3rem auto 5rem;

        &--ul {
          margin-left: 1rem;
          list-style: disc;
          & li {
            font-size: var(--font-normal);
            color: var(--color-text);
            margin: 1rem 0;
            text-align: justify;

            & a {
              text-decoration: underline;
              font-style: italic;
              color: var(--color-primary);
            }
          }
        }
      }
    }

    &--right {
      width: 50%;
      // background-color: brown;

      .right-intro__img {
        text-align: right;
      }

      .collage {
        width: 90%;
        margin-left: 5rem;
        transform: rotate(3deg);
        outline: 0.5rem solid var(--color-primary);
        outline-offset: 0.4rem;
        border-radius: 0.5rem;
      }
    }

    .right-skills {
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: space-evenly;

      .cert-title__link {
        font-size: var(--font-normal);
        color: var(--color-text);
        text-decoration: underline;
      }

      .certificate {
        width: 35%;
        // background-color: chartreuse;
        // display: flex;
        // align-items: center;
        // justify-content: center;

        &__img {
          width: 100%;
          outline: 0.5rem solid var(--color-primary);
          outline-offset: 0.5rem;
          border-radius: 0.4rem;
          transition: transform 0.2s ease-in;

          &:hover {
            transform: scale(1.03);
          }
          // display: block;
        }
      }
    }
  }

  @include respond(phone) {
    & {
      // empty
      &__content {
        // empty
        &--pair {
          flex-direction: column;
        }

        &--left {
          width: 100%;
          margin-bottom: 2rem;
        }

        &--right {
          width: 100%;

          .right-intro__img {
            text-align: center;

            .blob2 {
              width: 55%;
              align-self: flex-start;
            }
          }

          .right-skills {
            flex-direction: column;

